import React, { createContext, useContext, useState } from 'react';

// Create AuthContext
const AuthContext = createContext();

// Create a custom hook to use AuthContext
export const useAuth = () => {
    return useContext(AuthContext);
};

// AuthProvider component to provide authentication status globally
export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Check if the user is authenticated (you can store token in localStorage)
    const checkAuth = () => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
        } else {
            setIsAuthenticated(false);
        }
    };

    // Login function
    const login = (token, name) => {
        setIsAuthenticated(true);
        localStorage.setItem('token', token);
        localStorage.setItem('candidateName', name);
    };

    // Logout function
    const logout = () => {
        setIsAuthenticated(false);
        localStorage.removeItem('token');
        localStorage.removeItem('candidateName');
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, checkAuth, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
