import React, { useState, useEffect } from "react";
import axios from "axios";

const CandidateDashboard = () => {
  const [projects, setProjects] = useState([]);
  const [candidateName, setCandidateName] = useState(localStorage.getItem('candidateName')); // Get the candidate name from localStorage

  // Fetch projects assigned to the candidate
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:5005/api/projects/candidates/${candidateName}`);
        setProjects(response.data);
      } catch (err) {
        console.error("Error fetching projects:", err);
      }
    };

    if (candidateName) {
      fetchProjects();
    }
  }, [candidateName]);

  // Accept a project
  const acceptProject = async (projectId) => {
    try {
      await axios.put(`http://localhost:5005/api/projects/${projectId}/accept`);
      setProjects((prevProjects) =>
        prevProjects.map((project) =>
          project._id === projectId ? { ...project, status: 'Accepted' } : project
        )
      );
    } catch (err) {
      console.error("Error accepting project:", err);
    }
  };

  // Decline a project
  const declineProject = async (projectId) => {
    try {
      await axios.put(`http://localhost:5005/api/projects/${projectId}/decline`);
      setProjects((prevProjects) =>
        prevProjects.map((project) =>
          project._id === projectId ? { ...project, status: 'Declined' } : project
        )
      );
    } catch (err) {
      console.error("Error declining project:", err);
    }
  };

  return (
    <div>
      <h1>Candidate Dashboard</h1>
      <h2>Welcome, {candidateName}</h2>

      {projects.length > 0 ? (
        projects.map((project) => (
          <div key={project._id} style={{ border: "1px solid gray", margin: "10px", padding: "10px" }}>
            <h3>{project.title}</h3>
            <p><strong>Description:</strong> {project.description}</p>
            <p><strong>Progress:</strong> {project.progress}%</p>
            <p><strong>Status:</strong> {project.status}</p>
            <p><strong>Score:</strong> {project.score}</p>

            {project.status === "Pending" && (
              <div>
                <button onClick={() => acceptProject(project._id)}>Accept</button>
                <button onClick={() => declineProject(project._id)}>Decline</button>
              </div>
            )}
          </div>
        ))
      ) : (
        <p>No projects assigned to you.</p>
      )}
    </div>
  );
};

export default CandidateDashboard;
