import React, { createContext, useState, useContext } from 'react';

// Create Context
const AuthContext = createContext();

// Create a custom hook to use the AuthContext
export const useAuth = () => {
    return useContext(AuthContext);
};

// Provider component to wrap around your app
export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Check if the user is logged in (you could also check for a token in localStorage)
    const checkAuth = () => {
        const token = localStorage.getItem('authToken'); // or check your session, etc.
        if (token) {
            setIsAuthenticated(true);
        }
    };

    // Login function
    const login = () => {
        setIsAuthenticated(true);
        localStorage.setItem('authToken', 'your-token'); // Save token to localStorage
    };

    // Logout function
    const logout = () => {
        setIsAuthenticated(false);
        localStorage.removeItem('authToken');
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, checkAuth, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
