import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CandidateDashboard = () => {
    const [projects, setProjects] = useState([]);

    // Fetch candidate's assigned projects
    const fetchProjects = async () => {
        try {
            const response = await axios.get('http://localhost:5005/api/projects');
            setProjects(response.data.filter((p) => p.candidateName === "John Doe")); // Replace with dynamic candidateName
        } catch (error) {
            console.error("Error fetching projects:", error);
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    // Accept a project
    const acceptProject = async (id) => {
        try {
            await axios.put(`http://localhost:5005/api/projects/${id}/accept`);
            fetchProjects();
        } catch (error) {
            console.error("Error accepting project:", error);
        }
    };

    // Update project progress
    const updateProgress = async (id, progress) => {
        try {
            await axios.put(`http://localhost:5005/api/projects/${id}`, { progress });
            fetchProjects();
        } catch (error) {
            console.error("Error updating progress:", error);
        }
    };

    return (
        <div>
            <h1>Candidate Dashboard</h1>
            {projects.length === 0 ? (
                <p>No projects assigned to you.</p>
            ) : (
                projects.map((project) => (
                    <div key={project._id} style={{ border: '1px solid black', padding: '10px' }}>
                        <h3>{project.title}</h3>
                        <p>{project.description}</p>
                        <p>Status: {project.status}</p>
                        <p>Progress: {project.progress}%</p>
                        <p>Score: {project.score}</p>

                        {project.status === "Pending" && (
                            <button onClick={() => acceptProject(project._id)}>Accept Project</button>
                        )}

                        <div>
                            <label>Progress:</label>
                            <input
                                type="range"
                                min="0"
                                max="100"
                                value={project.progress}
                                onChange={(e) => updateProgress(project._id, e.target.value)}
                            />
                        </div>
                    </div>
                ))
            )}
        </div>
    );
};

export default CandidateDashboard;
